import React from 'react';
import './App.css';
import api from "./actions/api.js";
import ButterToast, { Cinnamon } from "butter-toast";
import { AssignmentTurnedIn , ExtensionSharp } from "@material-ui/icons";

const initialState = {
    email: "",
    password: "",
    emailError: "",
    passwordError: ""
}

class Login extends React.Component {
    
    state =initialState;

    handleChange = e => {
        const isCheckbox = e.target.type === "checkbox";
        this.setState({
            [e.target.name]: isCheckbox
                ? e.target.checked
                : e.target.value
        });
    }

    handleSubmit = e => {
        e.preventDefault();
        const isValid = this.validate();
        if(isValid){
            console.log(this.state);
            api.registerUser().fetchAll().then(res => {
                const user = res.data.filter( user => user.email===this.state.email);
                if(user.length===1){
                    console.log
                }else{
                    ButterToast.raise({
                        content: <Cinnamon.Crisp title="Email Error"
                            content="This Email No Register!"
                            scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                            icon={<ExtensionSharp />}
                        />
                    });
                }
            });
        }
    }

    validate = () => {
        let emailError = "";
        let passwordError = "";

        if(!this.state.password){
            passwordError="Password Cannot Be Blank"
        }

        if(!this.state.email){
            emailError="Email Cannot Be Blank"
        }else if(!this.state.email.includes('@')||!this.state.email.includes('.')){
            emailError = "Invalid Email!";
        }

        if(emailError || passwordError){
            this.setState({ emailError , passwordError});
            return false;
        }

        return true;
    }

    render (){
        return (
            <div class="container">
            <br></br><br></br>
                <div class="row justify-content-center">
                    <div class="col-md-8">
                        <div class="card">
                            <div class="card-header">Login</div>
                            <div class="card-body">
                                <form autoComplete="off" onSubmit={this.handleSubmit}>
                                    <div class="form-group row">
                                        <label for="email_address" class="col-md-4 col-form-label text-md-right">Email</label>
                                        <div class="col-md-6">
                                            <input type="text" class="form-control" name="email" value={this.state.email} onChange={this.handleChange} />
                                            <div style={{color : "red"}}>{this.state.emailError}</div>
                                        </div>
                                    </div>

                                    <div class="form-group row">
                                        <label for="password" class="col-md-4 col-form-label text-md-right">Password</label>
                                        <div class="col-md-6">
                                            <input type="password" class="form-control" name="password" value={this.state.password} onChange={this.handleChange} />
                                            <div style={{color : "red"}}>{this.state.passwordError}</div>
                                        </div>
                                    </div>
                                            
                                    <div class="col-md-6 offset-md-4">
                                        <button type="submit" class="btn btn-primary">
                                            Login
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Login;
