import React from 'react';
import '../App.css';
import api from "../actions/api.js";
import ButterToast, { Cinnamon } from "butter-toast";
import { AssignmentTurnedIn , ExtensionSharp } from "@material-ui/icons";

const initialState = {
    name: "",
    nameError: "",
    categories: []
}

class wishList extends React.Component {

    constructor(props) {
        super(props);
        this.state = initialState;
    }

    componentDidMount() {
        const url = "http://localhost:4000/category";
        fetch(url).then(response => response.json())
        .then(json => this.setState({categories: json}))
    }

    handleChange = e => {
        const isCheckbox = e.target.type === "checkbox";
        this.setState({
            [e.target.name]: isCheckbox
                ? e.target.checked
                : e.target.value
        });
    }

    onDelete(id){
        if (window.confirm("Are you sure to delete this record?")) {
            api.category().delete(id)
            .then(res =>{
                ButterToast.raise({
                    content: <Cinnamon.Crisp title="Online Store"
                        content="Delete Successful!"
                        scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                        icon={<AssignmentTurnedIn />}
                    />
                })
                this.componentDidMount()
            });
        }
    }

    handleSubmit = e => {
        e.preventDefault();
        const isValid = this.validate();
        if(isValid){
            console.log(this.state);
            api.category().create(this.state)
            .then(res => {
                ButterToast.raise({
                    content: <Cinnamon.Crisp title="Online Store"
                        content="Wishlist successfully"
                        scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                        icon={<AssignmentTurnedIn />}
                    />
                })
                this.setState(initialState)
                this.componentDidMount()
            } );
        }
    }

    validate = () => {
        let nameError = "";

        if(!this.state.name){
            nameError="Wishlist Name Cannot Be Blank"
        }

        if(nameError){
            this.setState({ nameError });
            return false;
        }

        return true;
    }

    render (){
        if(localStorage.getItem('userEmail')){
            const { categories } = this.state;
            return (
                <div class="container">
                <br></br><br></br>
                    <div class="row justify-content-center">
                        <div class="col-md-10">
                            <div class="card">
                                <div class="card-header">Wishlist</div>
                                <div class="card-body">
                                    <form autoComplete="off" onSubmit={this.handleSubmit}>
                                        <div class="form-group row">
                                            <label class="col-md-4 col-form-label text-md-right">Wishlist Name</label>
                                            <div class="col-md-6">
                                                <input type="text" class="form-control" name="name" value={this.state.name} onChange={this.handleChange} />
                                                <div style={{color : "red"}}>{this.state.nameError}</div>
                                            </div>
                                        </div>
                                                
                                        <div class="col-md-4 offset-md-4">
                                            <button type="submit" class="btn btn-primary" >
                                                Add
                                            </button>
                                        </div>
                                    </form>
                                    <br></br>
                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th class="tableTh">Category Name</th>
                                                <th class="tableTh">Remove</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                        {

                                            categories.map((category) =>

                                            <tr>
                                                <td class="tableTh">{ category.name }</td>
                                                <td class="tableTh"><button type='button' onClick={() => this.onDelete(category._id)} class='btn btn-danger'>Delete</button></td>
                                            </tr>
                                        )}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default wishList;
