import React from 'react';
import '../App.css';
import api from "../actions/api.js";
import ButterToast, { Cinnamon } from "butter-toast";
import { AssignmentTurnedIn , ExtensionSharp } from "@material-ui/icons";

const initialState = {
    name: "",
    nameError: ""
}

class Category extends React.Component {

    state =initialState;

    handleChange = e => {
        const isCheckbox = e.target.type === "checkbox";
        this.setState({
            [e.target.name]: isCheckbox
                ? e.target.checked
                : e.target.value
        });
    }

    handleSubmit = e => {
        e.preventDefault();
        const isValid = this.validate();
        if(isValid){
            console.log(this.state);
            api.category().fetchAll().then(res => {
                const cat = res.data.filter( cat => cat.name===this.state.name);
                if(cat.length>0){
                    ButterToast.raise({
                        content: <Cinnamon.Crisp title="Online Store"
                            content="This Catgory Name Already Exists!"
                            scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                            icon={<ExtensionSharp />}
                        />
                    });
                }else{
                    api.category().create(this.state)
                    .then(res => {
                        ButterToast.raise({
                            content: <Cinnamon.Crisp title="Online Store"
                                content="Category Add successfully"
                                scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                                icon={<AssignmentTurnedIn />}
                            />
                        })
                        this.setState(initialState)
                    } );
                }
            });
        }
    }

    validate = () => {
        let nameError = "";

        if(!this.state.name){
            nameError="Category Name Cannot Be Blank"
        }

        if(nameError){
            this.setState({ nameError });
            return false;
        }

        return true;
    }

    render (){
        if(localStorage.getItem('userEmail')){
            return (
                <div class="container">
                <br></br><br></br>
                    <div class="row justify-content-center">
                        <div class="col-md-10">
                            <div class="card">
                                <div class="card-header">Category</div>
                                <div class="card-body">
                                    <form autoComplete="off" onSubmit={this.handleSubmit}>
                                        <div class="form-group row">
                                            <label class="col-md-4 col-form-label text-md-right">Category Name</label>
                                            <div class="col-md-6">
                                                <input type="text" class="form-control" name="name" value={this.state.name} onChange={this.handleChange} />
                                                <div style={{color : "red"}}>{this.state.nameError}</div>
                                            </div>
                                        </div>
                                                
                                        <div class="col-md-4 offset-md-4">
                                            <button type="submit" class="btn btn-primary">
                                                Add
                                            </button>
                                        </div>
                                    </form>
                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th class="tableTh">Category Name</th>
                                                <th class="tableTh">Last Name</th>
                                                <th class="tableTh">Email</th>
                                                <th class="tableTh">Phone</th>
                                                <th class="tableTh">User Type</th>
                                                <th class="tableTh">Change</th>
                                                <th class="tableTh">Remove User</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                        {

                                            categories.map((user) =>

                                            <tr>
                                                <td class="tableTh">{ user.fname }</td>
                                                <td class="tableTh">{ user.lname }</td>
                                                <td class="tableTh">{ user.email }</td>
                                                <td class="tableTh">{ user.phone }</td>
                                                <td class="tableTh">{ user.type }</td>
                                                <td class="tableTh">{this.editButton(user.type,user._id,user.email)}</td>
                                                <td class="tableTh"><button type='button' onClick={() => this.onDelete(user._id)} class='btn btn-danger'>Delete</button></td>
                                            </tr>
                                        )}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default Category;
