import React from 'react';
import '../App.css';

class Category extends React.Component {

    state =initialState;

    handleChange = e => {
        const isCheckbox = e.target.type === "checkbox";
        this.setState({
            [e.target.name]: isCheckbox
                ? e.target.checked
                : e.target.value
        });
    }

    handleSubmit = e => {
        e.preventDefault();
        const isValid = this.validate();
        if(isValid){
            console.log(this.state);
            
        }
    }

    validate = () => {
        let nameError = "";

        if(!this.state.name){
            nameError="Category Name Cannot Be Blank"
        }

        if(nameError){
            this.setState({ nameError });
            return false;
        }

        return true;
    }

    render (){
        if(localStorage.getItem('userEmail')){
            return (
                <div class="container">
                <br></br><br></br>
                    <div class="row justify-content-center">
                        <div class="col-md-10">
                            <div class="card">
                                <div class="card-header">Category</div>
                                <div class="card-body">
                                    <form autoComplete="off" onSubmit={this.handleSubmit}>
                                        <div class="form-group row">
                                            <label class="col-md-4 col-form-label text-md-right">Category Name</label>
                                            <div class="col-md-6">
                                                <input type="text" class="form-control" name="fname" value={this.state.name} onChange={this.handleChange} />
                                                <div style={{color : "red"}}>{this.state.nameError}</div>
                                            </div>
                                        </div>
                                                
                                        <div class="col-md-4 offset-md-4">
                                            <button type="submit" class="btn btn-primary">
                                                Register
                                            </button>
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default Category;
