import React from 'react';
import '../App.css';
import comApi from "../actions/commentApi.js";
import cartApi from "../actions/cartApi.js";
import ButterToast, { Cinnamon } from "butter-toast";
import { AssignmentTurnedIn , ExtensionSharp } from "@material-ui/icons";

const initialState = {
    products: [],
    messages: [],
    confirmButton: "Send",
    proId: "",
    proName: "",
    proPrice: "",
    proDiscount: "",
    proQuantity : "",
    proImage : "",
    quantity : "",
    type:"",
    total:"",
    quantityError:""
}

class oneProduct extends React.Component {

    constructor(props) {
        super(props);
        this.state = initialState;
    }

    handleChange = e => {
        const isCheckbox = e.target.type === "checkbox";
        this.setState({
            [e.target.name]: isCheckbox
                ? e.target.checked
                : e.target.value
        });
    }

    onClear(){
        this.setState(initialState);
        this.componentDidMount();
    }

    onChange(id,msg){
        this.setState({confirmButton:"EDIT",commentId:id,comment:msg});
    }

    componentDidMount() {
        const purl = "http://localhost:3500/product";
        fetch(purl).then(response => response.json())
        .then(json => {const pro = json.filter(pro => pro._id===localStorage.getItem('itemId'))
            this.setState({products: pro,proId:pro[0]['_id'],proName:pro[0]['name'],proPrice:pro[0]['price'],proDiscount:pro[0]['discount'],proQuantity:pro[0]['quantity'],proImage:pro[0]['image']})
        })
    }

    onCart(){
        if(this.state.quantity){
            if(this.state.quantity<=this.state.proQuantity){
                const total=this.state.quantity*this.state.proPrice-(this.state.proDiscount*this.state.quantity)
                this.setState({ type:'cart' ,total:total , email: localStorage.getItem('userEmail') }, () => { 
                    cartApi.cart().create(this.state)
                    .then(res =>{
                        ButterToast.raise({
                            content: <Cinnamon.Crisp title="Online Store"
                                content="Product Add Successful!"
                                scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                                icon={<AssignmentTurnedIn />}
                            />
                        })
                        this.componentDidMount()
                        this.setState(initialState)
                    })
                })
            }else{
                this.setState({quantityError:"Quantity Error!"})
            }
        }else{
            this.setState({quantityError:"Quantity Required!"})
        }
    }

    editButton(id,msg,email){
        if(localStorage.getItem('userEmail')){
            if(email===localStorage.getItem('userEmail')){
                return  [<button type='button' onClick={() => this.onChange(id,msg)} class='btn btn-success'>EDIT</button>,
                        <button type='button' onClick={() => this.onDelete(id)} class='btn btn-danger'>Delete</button>];
            }
        }
    }

    onDelete(id){
        if (window.confirm("Are you sure to delete comment?")) {
            comApi.comment().delete(id)
            .then(res =>{
                ButterToast.raise({
                    content: <Cinnamon.Crisp title="Online Store"
                        content="Delete Successful!"
                        scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                        icon={<AssignmentTurnedIn />}
                    />
                })
                this.componentDidMount()
            });
        }
    }

    render (){
        const { products } = this.state;
        return (
            <div class="container">
            <br></br><br></br>
                <div class="row justify-content-center">
                    <div class="col-md-12">
                        <div class="card">
                            <div class="card-body">
                                <table class="table">
                                    <tbody>
                                    {
                                        products.map((product) =>

                                        <tr>
                                            <td class="tableTh" width="35%"><img width="200px" alt="" src={ "http://localhost:3500/"+product.image } class="img-thumbnail" /></td>
                                            <td class="tableTh" width="65%"><h3>{ product.name  }</h3><br/><h5>category :{ product.name  } / Price: Rs. { product.price }</h5>
                                            <br/><h5>Discount : Rs. { product.discount }</h5>
                                            <br/><h5>Available Quantity : { product.quantity }</h5>
                                            <br/>Quantity : <input type='text' name="quantity" value={this.state.quantity} onChange={this.handleChange} style={{width: "50px"}} /> / { product.quantity }
                                            <div style={{color : "red"}}>{this.state.quantityError}</div><br/>
                                            <br/><button type='button' onClick={() => this.onCart()} class='btn btn-success'>Add to Cart</button>
                                            </td>
                                        </tr>
                                    )}
                                    </tbody>
                                </table>
                                <hr/>
                                <form autoComplete="off" onSubmit={this.handleSubmit}>
                                    <div class="form-group row">
                                        <label class="col-md-4 col-form-label text-md-right">Your Comment</label>
                                        <div class="col-md-6">
                                            <input type="text" class="form-control" name="comment" value={this.state.comment} onChange={this.handleChange} />
                                            <div style={{color : "red"}}>{this.state.commentError}</div>
                                        </div>
                                    </div>
                                            
                                    <div class="col-md-4 offset-md-4">
                                        <input type="submit" class="btn btn-primary" value={this.state.confirmButton} />
                                        <input type="button" class="btn btn-danger" value="Clear" onClick={() => this.onClear()} />
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default oneProduct;
