import React from 'react';
import '../App.css';

const initialState = {
    products: [],
    comment: "",
    confirmButton: "Send"
}

class oneProduct extends React.Component {

    constructor(props) {
        super(props);
        this.state = initialState;
    }

    onClear(){
        this.setState(initialState);
        this.componentDidMount();
    }

    componentDidMount() {
        const purl = "http://localhost:4000/product";
        fetch(purl).then(response => response.json())
        .then(json => {const pro = json.filter(pro => pro._id===localStorage.getItem('itemId'))
            this.setState({products: pro})
        })
    }

    onBuy(id){
        localStorage.setItem("itemId",id);
        window.location.href = '/product';
    }

    handleSubmit = e => {
        e.preventDefault();
        const isValid = this.validate();
        if(isValid){
            console.log(this.state);
            
        }
    }

    validate = () => {
        let nameError = "";
        let priceError = "";
        let discountError = "";
        let categoryError = "";
        let quantityError = "";
        let imageError = "";

        if(!this.state.name){
            nameError="Product Name Cannot Be Blank"
        }

        if(!this.state.image){
            imageError="Image Required!"
        }

        if(!this.state.price){
            priceError="Price Cannot Be Blank"
        }else if(isNaN(this.state.price)){
            priceError = "Use only digits!";
        }

        if(!this.state.discount){
            discountError="Discount Cannot Be Blank"
        }else if(isNaN(this.state.discount)){
            discountError = "Use only digits!";
        }

        if(!this.state.category){
            categoryError="seelect category!"
        }

        if(!this.state.quantity){
            quantityError="Quantity Cannot Be Blank"
        }else if(isNaN(this.state.quantity)){
            quantityError = "Use only digits!";
        }

        if(nameError||quantityError||categoryError||discountError||priceError||imageError){
            this.setState({ nameError ,quantityError ,categoryError,discountError,priceError,imageError});
            return false;
        }else{
            this.setState({ nameError ,quantityError ,categoryError,discountError,priceError,imageError});
        }

        return true;
    }

    render (){
        if(localStorage.getItem('userEmail')){
            const { products } = this.state;
            return (
                <div class="container">
                <br></br><br></br>
                    <div class="row justify-content-center">
                        <div class="col-md-12">
                            <div class="card">
                                <div class="card-body">
                                    <table class="table">
                                        <tbody>
                                        {
                                            products.map((product) =>

                                            <tr>
                                                <td class="tableTh" width="35%"><img width="200px" alt="" src={ "http://localhost:4000/"+product.image } class="img-thumbnail" /></td>
                                                <td class="tableTh" width="65%"><h3>{ product.name  }</h3><br/><h5>category :{ product.name  } / Price: Rs. { product.price }</h5>
                                                <br/><h5>Discount : Rs. { product.discount }</h5>
                                                <br/><h5>Available Quantity : { product.quantity }</h5>
                                                <br/>Quantity : <input type='text' style={{width: "50px"}} />/{ product.quantity }<br/>
                                                <br/><button type='button' onClick={() => this.onBuy(product._id)} class='btn btn-success'>BUY</button>
                                                </td>
                                            </tr>
                                        )}
                                        </tbody>
                                    </table>
                                    <hr/>
                                    <form autoComplete="off" onSubmit={this.handleSubmit}>
                                        <div class="form-group row">
                                            <label class="col-md-4 col-form-label text-md-right">Your Comment</label>
                                            <div class="col-md-6">
                                                <input type="text" class="form-control" name="comment" value={this.state.comment} onChange={this.handleChange} />
                                                <div style={{color : "red"}}>{this.state.commentError}</div>
                                            </div>
                                        </div>
                                               
                                        <div class="col-md-4 offset-md-4">
                                            <input type="submit" class="btn btn-primary" value={this.state.confirmButton} />
                                            <input type="button" class="btn btn-danger" value="Clear" onClick={() => this.onClear()} />
                                        </div>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default oneProduct;
