import React from 'react';
import '../App.css';

const initialState = {
    products: []
}

class ProductList extends React.Component {

    constructor(props) {
        super(props);
        this.state = initialState;
    }

    componentDidMount() {
        const purl = "http://localhost:3500/product";
        fetch(purl).then(response => response.json())
        .then(json => this.setState({products: json}))
    }

    onBuy(id){
        localStorage.setItem("itemId",id);
        window.location.href = '/viewProduct';
    }

    render (){
        const { products } = this.state;
        return (
            <div class="container">
            <br/><br/>
                <div class="row justify-content-center">
                    <h1>All Products</h1>
                        <table class="table">
                            <tbody>
                            {
                                products.map((product) =>

                                <tr>
                                    <td width="25%"><img width="200px" alt="" src={ "http://localhost:3500/"+product.image } onClick={() => this.onBuy(product._id)} class="img-thumbnail" /></td>
                                    <td width="75%"><h3 st onClick={() => this.onBuy(product._id)} >{ product.name  }</h3><br/><h5>category :{ product.category  } / Price: Rs. { product.price }</h5>
                                        <p>Description : { product.description  }</p>
                                    </td>
                                </tr>
                            )}
                            </tbody>
                        </table>
                </div>
            </div>
        );
    }
}

export default ProductList;
