import React from 'react';
import '../App.css';
import api from "../actions/api.js";
import ButterToast, { Cinnamon } from "butter-toast";
import { AssignmentTurnedIn } from "@material-ui/icons";

class Users extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            users: []
        }
    }

    componentDidMount() {
        const url = "http://localhost:4000/register";
        fetch(url).then(response => response.json())
        .then(json => this.setState({users: json.filter(user => user.email!==localStorage.getItem('userEmail'))}))
    }

    onDelete(id){
        if (window.confirm("Are you sure to delete this record?")) {
            api.registerUser().delete(id)
            .then(res =>{
                ButterToast.raise({
                    content: <Cinnamon.Crisp title="Online Store"
                        content="Delete Successful!"
                        scheme={Cinnamon.Crisp.SCHEME_PURPLE}
                        icon={<AssignmentTurnedIn />}
                    />
                })
                this.componentDidMount()
            });
        }
    }

    editBuon(type){
        if(type=="user"){
            return  <button type='button' onclick='edit("+visit.getId()+")' class='btn btn-success'>Edit</button>
        }
    }

    render (){
        if(localStorage.getItem('userEmail')){
            const {users} = this.state;
            return (
                <div class="container">
                <br></br><br></br>
                    <div class="row justify-content-center">
                        <div class="col-md-12">
                            <div class="card">
                                <div class="card-header">Users</div>
                                <div class="card-body">
                                    <table class="table">
                                        <thead>
                                            <tr>
                                                <th class="tableTh">First Name</th>
                                                <th class="tableTh">Last Name</th>
                                                <th class="tableTh">Email</th>
                                                <th class="tableTh">Phone</th>
                                                <th class="tableTh">User Type</th>
                                                <th class="tableTh">Change</th>
                                                <th class="tableTh">Remove User</th>
                                            </tr>
                                        </thead>
                                        <tbody>
                                        {

                                            users.map((user) =>

                                            <tr>
                                                <td class="tableTh">{ user.fname }</td>
                                                <td class="tableTh">{ user.lname }</td>
                                                <td class="tableTh">{ user.email }</td>
                                                <td class="tableTh">{ user.phone }</td>
                                                <td class="tableTh">{ user.type }</td>
                                                <td class="tableTh"></td>
                                                <td class="tableTh"><button type='button' onClick={() => this.onDelete(user._id)} class='btn btn-danger'>Delete</button></td>
                                            </tr>
                                        )}
                                        </tbody>
                                    </table>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            );
        }
    }
}

export default Users;
