import React from 'react';
import './App.css';

const initialState = {
    email: "",
    password: "",
    emailError: "",
    passwordError: ""
}

class Login extends React.Component {
    
    state =initialState;

    handleChange = e => {
        const isCheckbox = e.target.type === "checkbox";
        this.setState({
            [e.target.name]: isCheckbox
                ? e.target.checked
                : e.target.value
        });
    }

    handleSubmit = e => {
        e.preventDefault();
        const isValid = this.validate();
        if(isValid){
            console.log(this.state);
            this.setState(initialState);
        }
    }

    validate = () => {
        let emailError = "";
        let passwordError = "";

        if(!passwordError){
            passwordError="Password Canot"
        }

        if(!this.state.email.includes('@')){
            emailError = "Invalid Email!";
        }

        if(emailError){
            this.setState({ emailError });
            return false;
        }

        return true;
    }

    render (){
        return (
            <div class="container">
            <br></br><br></br>
                <div class="row justify-content-center">
                    <div class="col-md-8">
                        <div class="card">
                            <div class="card-header">Login</div>
                            <div class="card-body">
                                <form autoComplete="off" onSubmit={this.handleSubmit}>
                                    <div class="form-group row">
                                        <label for="email_address" class="col-md-4 col-form-label text-md-right">Email</label>
                                        <div class="col-md-6">
                                            <input type="text" class="form-control" name="email" value={this.state.email} onChange={this.handleChange} />
                                            <div style={{color : "red"}}>{this.state.emailError}</div>
                                        </div>
                                    </div>

                                    <div class="form-group row">
                                        <label for="password" class="col-md-4 col-form-label text-md-right">Password</label>
                                        <div class="col-md-6">
                                            <input type="password" class="form-control" name="password" value={this.state.password} onChange={this.handleChange} />
                                            <div style={{color : "red"}}>{this.state.passwordError}</div>
                                        </div>
                                    </div>
                                            
                                    <div class="col-md-6 offset-md-4">
                                        <button type="submit" class="btn btn-primary">
                                            Login
                                        </button>
                                    </div>
                                </form>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Login;
